                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                      </svg>
                      <div>
                        <p className="text-lg font-bold text-green-800">âœ… Payment Complete!</p>
                        <p className="text-sm text-green-700">Total amount of â‚¹{formData.totalAmount.toLocaleString()} has been fully paid.</p>
                      </div>
                    </div>
                  </div>
                )}

                {/* Summary Fields */}
                <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mt-4">
                  <div>
                    <label className="label">Total Amount (â‚¹) *</label>
                    <input
                      type="number"
                      name="totalAmount"
                      min="0"
                      className="input-field"
                      value={formData.totalAmount}
                      onChange={handleChange}
                      placeholder="12000"
                      required
                    />
                    <p className="text-xs text-gray-500 mt-1">
                      Total job cost (e.g., 12,000)
                    </p>
                  </div>

                  <div>
                    <label className="label">Total Paid (â‚¹)</label>
                    <div className={`input-field font-semibold ${
                      isFullyPaid() 
                        ? 'bg-green-100 text-green-700 border-green-300' 
                        : 'bg-blue-50 text-blue-700'
                    }`}>
                      â‚¹{calculatePaymentPaid().toLocaleString()}
                    </div>
                    <p className="text-xs text-gray-500 mt-1">
                      Sum of all payments
                    </p>
                  </div>

                  <div>
                    <label className="label">Pending Amount (â‚¹)</label>
                    <div className={`input-field font-semibold ${
                      calculatePaymentDue() === 0 
                        ? 'bg-green-50 text-green-700' 
                        : 'bg-red-50 text-red-700'
                    }`}>
                      â‚¹{calculatePaymentDue().toLocaleString()}
                    </div>
                    <p className="text-xs text-gray-500 mt-1">
                      Auto: Total - Paid
                    </p>
                  </div>

                  <div>
                    <label className="label">Due Date</label>
                    <input
                      type="date"
                      name="dueDate"
                      className="input-field"
                      value={formData.dueDate}
                      onChange={handleChange}
                    />
                  </div>
                </div>
              </div>

              {/* Photo Upload Section */}
              <div className="border-t pt-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Photos for Identification</h3>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* House Photo */}
                  <div>
                    <label className="label">House Photo</label>
                    <div className="mt-2">
                      {housePhotoPreview ? (
                        <div className="relative">
                          <img
                            src={housePhotoPreview}
                            alt="House preview"
                            className="w-full h-48 object-cover rounded-lg border border-gray-300"
